import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import {Form, Button, Col, fieldset, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component {
  constructor() 
  {
    super();
    this.state={
      fname: "",
      lname: "",
      email: "",
      password: "",
      range: "",
      choosefile: "",
      radio: "",
      fnameError: "",
      lnameError: "",
      emailError: "",
      passwordError: "",
      rangeError: "",
      choosefileError: "",
      radioError: "",
    }
  }

  valid() 
  {
    if(!this.state.fname.includes("@")) 
    {
      this.setState({fnameError: "Invaild First Name"})
    }
    if(!this.state.lname.includes("@")) 
    {
      this.setState({lnameError: "Invaild Last Name"})
    }
    if(!this.state.email.includes("@") && this.state.password.length < 5) 
    {
      this.setState(
        {emailError: "Invaild Email Address", passwordError: "Password length should be more than 5"}
      )
    }
    if(!this.state.range.includes("@")) 
    {
      this.setState({rangeError: "Invaild Range"})
    }
    if(!this.state.choosefile.includes("@")) 
    {
      this.setState({choosefileError: "Please Choose File"})
    }
    if(!this.state.radio.includes("@")) 
    {
      this.setState({radioError: "Please Select Radios"})
    }
  }

  submit() 
  {
    if(this.valid()) {
      alert("form has been submited")
    }
  }

  render() {
    return (
      <div className="App">
        <h1>Welcome to Contact Form</h1>
        <Form>
          <Form.Row>
            <Col>
              <Form.Control placeholder="First name" onChange={(event)=>{this.setState({fname:event.target.value})}} />
              <p style={{color: "#f00", fontSize: "13px",}}>{this.state.fnameError}</p>
            </Col>
            <Col>
              <Form.Control placeholder="Last name" onChange={(event)=>{this.setState({lname:event.target.value})}} />
              <p style={{color: "#f00", fontSize: "13px",}}>{this.state.lnameError}</p>
            </Col>
          </Form.Row>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" onChange={(event)=>{this.setState({email:event.target.value})}} />
            <p style={{color: "#f00", fontSize: "13px",}}>{this.state.emailError}</p>
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" onChange={(event)=>{this.setState({password:event.target.value})}} />
            <p style={{color: "#f00", fontSize: "13px",}}>{this.state.passwordError}</p>
          </Form.Group>
          <Form.Group controlId="formBasicRange">
            <Form.Label>Range</Form.Label>
            <Form.Control type="range" />
            <p style={{color: "#f00", fontSize: "13px",}}>{this.state.rangeError}</p>
          </Form.Group>
          <fieldset>
            <Form.Group as={Row}>
              <Form.Label as="legend" column sm={2}>
                Radios
              </Form.Label>
              <Col sm={10}>
                <Form.Check
                  type="radio"
                  label="first radio"
                  name="formHorizontalRadios"
                  id="formHorizontalRadios1"
                />
                <Form.Check
                  type="radio"
                  label="second radio"
                  name="formHorizontalRadios"
                  id="formHorizontalRadios2"
                />
                <Form.Check
                  type="radio"
                  label="third radio"
                  name="formHorizontalRadios"
                  id="formHorizontalRadios3"
                />
              </Col>
              <p style={{color: "#f00", fontSize: "13px",}}>{this.state.radioError}</p>
            </Form.Group>
          </fieldset>
          <Form.Group>
            <Form.File id="exampleFormControlFile1" label="Example file input" />
            <p style={{color: "#f00", fontSize: "13px",}}>{this.state.choosefileError}</p>
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="button" onClick={()=>this.submit()} >
            Submit
          </Button>
        </Form>
      </div>
    );
  }
}

export default App;
